import Counter from "./components/Counter";
import React, { Component } from "react";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import characters from "./characters.json";
import CharacterCard from "./components/FriendCard";

class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    characters: characters,
    countedCharacters: []
  };

  countCharacter = id => {
    // Filter this.state.friends for friends with an id not equal to the id being removed
    const countedCharacters = this.state.characters.filter(character => character.id == id);
    // Set this.state.friends equal to the new friends array
    this.setState({ countedCharacters });

    console.log(this.state.countedCharacters)
  };

  shuffleArray = array => {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
    this.setState({ characters });
    console.log(characters);
}

  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (
      <Wrapper>
        <Counter />
        <Title>Friends List</Title>
        {this.state.characters.map(character => (
          <CharacterCard
            shuffleArray={this.countCharacter}
            id={character.id}
            key={characters.id}
            name={characters.name}
            image={characters.image}
          />
        ))}
      </Wrapper>
    );
  }
}



export default App;
