import Counter from "./components/Counter";
import React, { Component } from "react";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import characters from "./characters.json";
import CharacterCard from "./components/FriendCard";

class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    characters,
    countedCharacters: [],
    count: 0
  };

   // handleDecrement decreases this.state.count by 1
   handleDecrement = () => {
    // We always use the setState method to update a component's state
    this.setState({ count: this.state.count - 1 });
  };

  countCharacter = id => {

    console.log(this.state.countedCharacters.indexOf(id));
    // Filter this.state.friends for friends with an id not equal to the id being removed
    if (this.state.countedCharacters.indexOf(id) === -1) {
    const findCharacters = this.state.characters.filter(character => character.id === id);
    // Set this.state.friends equal to the new friends array
    const countedCharacters = this.state.countedCharacters;
    countedCharacters.push(findCharacters.id);


    this.setState({ 
      countedCharacters,
      count: this.state.count + 1
    });

    console.log(this.state.countedCharacters);
    console.log(this.state.count);
  }
  };

  shuffleArray = array => {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
    this.setState({ characters });
    console.log(characters);
}

  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (
      <Wrapper>
        <Counter count={this.state.count}/>
        <Title>Friends List</Title>
        {this.state.characters.map(character => (
          <CharacterCard
            shuffleArray={this.countCharacter}
            id={character.id}
            key={character.id}
            name={character.name}
            image={character.image}
          />
        ))}
      </Wrapper>
    );
  }
}



export default App;
